import React, { useState, useEffect } from 'react';
import { FaPlus, FaTasks } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import Task from '../Common/Task';

function Admin() {
  const [task, setTask] = useState('');
  const [userId, setUserId] = useState('');
  const [deadline, setDeadline] = useState('');
  const [users, setUsers] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [showForm, setShowForm] = useState(true);
  const [showTasks, setShowTasks] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/getusers');
        if (!response.ok) {
          throw new Error('Failed to fetch users');
        }
        const data = await response.json();
        setUsers(data);
      } catch (error) {
        console.error('Error:', error);
      }
    };

    const fetchTasks = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/gettasks');
        if (!response.ok) {
          throw new Error('Failed to fetch tasks');
        }
        const data = await response.json();
        setTasks(data);
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchUsers();
    fetchTasks();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const response = await fetch('http://localhost:5000/api/assigntask', {
        method: 'POST',
        headers: {
          'x-auth-token': token,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId, task, deadline }),
      });

      if (!response.ok) {
        throw new Error('Task assignment failed');
      }

      const data = await response.json();
      console.log('Task assigned successfully:', data);

      setTask('');
      setUserId('');
      setDeadline('');
      setShowForm(false);
      setShowTasks(true);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="flex">
      <div className="w-64 bg-gray-800 text-white flex flex-col p-4 fixed min-h-screen top-16">
        <h2 className="text-2xl font-bold mb-4">Admin Panel</h2>
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mb-4 flex items-center"
          onClick={() => {
            setShowForm(true);
            setShowTasks(false);
          }}
        >
          Task <FaPlus className="ml-2" />
        </button>
        <button
          className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded mb-4 flex items-center"
          onClick={() => {
            setShowTasks(true);
            setShowForm(false);
          }}
        >
          View Tasks <FaTasks className="ml-2" />
        </button>
      </div>
      <div className="flex-1 flex items-center justify-center">
        {showTasks ? (
          <Task />
        ) : showForm ? (
          <div className="bg-white p-8 rounded shadow-md w-96">
            <h1 className="text-3xl font-bold text-center mb-8">Create Task</h1>
            <form onSubmit={handleSubmit}>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="task">
                  Task
                </label>
                <input
                  type="text"
                  id="task"
                  value={task}
                  onChange={(e) => setTask(e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                  placeholder="Enter task"
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="userId">
                  Assign to User
                </label>
                <select
                  id="userId"
                  value={userId}
                  onChange={(e) => setUserId(e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                >
                  <option value="">Select a user</option>
                  {users.map((user) => (
                    <option key={user._id} value={user._id}>
                      {user.name}
                    </option>
                  ))}
                </select>
              </div>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="deadline">
                  Deadline
                </label>
                <input
                  type="date"
                  id="deadline"
                  value={deadline}
                  onChange={(e) => setDeadline(e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                />
              </div>
              <button
                type="submit"
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              >
                Assign Task
              </button>
            </form>
          </div>
        ) : null}
      </div>
    </div>
  );
}

export default Admin;
