import React, { useState, useEffect } from 'react';
import { FaCheck } from 'react-icons/fa';

const User = () => {
  const [tasks, setTasks] = useState([]);
  const [notes, setNotes] = useState({});
  const [completionDates, setCompletionDates] = useState({});

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/getusertasks');
        if (!response.ok) {
          throw new Error('Failed to fetch tasks');
        }
        const data = await response.json();
        setTasks(data);
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchTasks();
  }, []);

  const handleNoteChange = (taskId, value) => {
    setNotes({ ...notes, [taskId]: value });
  };

  const handleCompletionDateChange = (taskId, value) => {
    setCompletionDates({ ...completionDates, [taskId]: value });
  };

  return (
    <div className="p-4 mt-20">
      <h1 className="text-2xl font-bold mb-4">Task List</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {tasks.map((task, index) => (
          <div key={task._id} className="bg-white p-4 rounded shadow-md flex flex-col items-center">
            <h2 className="text-xl font-bold mb-2">{task.task}</h2>
            <p className="text-gray-600 mb-2">{task.email}</p>
            {task.completed ? (
              <div className="flex flex-col items-center">
                <div className="flex items-center">
                  <FaCheck className="text-green-500 text-2xl mr-2" />
                  <span className="text-green-500 font-bold">Completed</span>
                </div>
                <p className="text-gray-600 mb-2">{task.note}</p>
              </div>
            ) : (
              <div className="flex flex-col items-center">
                <input
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline mb-2"
                  value={notes[task._id] || ''}
                  onChange={(e) => handleNoteChange(task._id, e.target.value)}
                  placeholder="Enter note"
                />
                <input
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline mb-2"
                  value={completionDates[task._id] || ''}
                  onChange={(e) => handleCompletionDateChange(task._id, e.target.value)}
                  placeholder="Enter completion date"
                />
                <button
                  onClick={() => handleCompletionDateChange(task._id, completionDates[task._id])}
                  className="bg-green-500 text-white px-4 py-2 rounded"
                >
                  Complete Task
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default User;
