import React, { useEffect, useState } from 'react';

const User = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:3001/movies');
        const result = await response.json();
        setData(result.result); // Adjust based on the actual data structure
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md w-96">
        <h1 className="text-3xl font-bold text-center mb-8">User Dashboard</h1>
        {data ? (
          <div>
            <h2 className="text-2xl font-bold mb-4">Movies List</h2>
            {data.map((movie, index) => (
              <div key={index} className="mb-4">
                <p><strong>Title:</strong> {movie.title}</p>
                <p><strong>Genre:</strong> {movie.genre}</p>
                <p><strong>Director:</strong> {movie.director}</p>
                <p><strong>Starring:</strong> {movie.starring}</p>
                <p><strong>Release Date:</strong> {movie.releaseDate}</p>
                <p><strong>Language:</strong> {movie.language}</p>
                <p><strong>Votes:</strong> {movie.votes}</p>
              </div>
            ))}
          </div>
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </div>
  );
};

export default User;
