import React, { useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngleUp, faAngleDown } from '@fortawesome/free-solid-svg-icons';

const User = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:3001/movies'); // Replace with your API URL
        const result = await response.json();
        setData(result);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleVoteChange = (index, change) => {
    const updatedData = [...data]; // Copy the current data
    updatedData[index].voting += change; // Modify the vote count
    setData(updatedData); // Update the state with the new data
  };

  return (
    <div className="min-h-screen bg-gray-100 py-6">
      <h1 className="text-4xl font-bold mb-6 text-center">Movies List</h1>
      {/* Grid layout to display movie cards */}
      <div className="w-full grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {data ? (
          data.map((movie, index) => (
            <div
              key={index}
              className="flex flex-col bg-white shadow-md rounded-md p-6 gap-6"
              style={{ height: '500px', width: '450px' }} // Increased height and width for better spacing
            >
              {/* Movie Poster */}
              <div className="w-28 h-60 flex-shrink-0">
                <img
                  src={movie.poster}
                  alt={movie.title}
                  className="w-full h-full object-cover rounded-md"
                />
              </div>

              {/* Movie Details */}
              <div className="flex flex-col justify-between flex-grow gap-4">
                <h2 className="text-xl font-semibold">{movie.title}</h2>
                <p className="text-sm">
                  <strong>Genre:</strong> {movie.genre}
                </p>
                <p className="text-sm">
                  <strong>Director:</strong> {movie.director}
                </p>
                <p className="text-sm">
                  <strong>Starring:</strong> {movie.cast}
                </p>
                <p className="text-sm">
                  <strong>Release Date:</strong> {movie.release_date}
                </p>
                <p className="text-sm">
                  <strong>Language:</strong> {movie.language}
                </p>
                <p className="text-sm">
                  <strong>Votes:</strong> {movie.voting}
                </p>

                {/* Watch Trailer Button */}
                <div className="flex-shrink-0">
                  <button className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600">
                    Watch Trailer
                  </button>
                </div>
              </div>

              {/* Vote Buttons - Placing them right below the poster */}
              <div className="flex justify-between gap-2 mt-4">
                <button
                  onClick={() => handleVoteChange(index, 1)}
                  className="bg-green-500 text-white px-2 py-1 rounded-md hover:bg-green-600"
                >
                  <FontAwesomeIcon icon={faAngleUp} />
                </button>
                <button
                  onClick={() => handleVoteChange(index, -1)}
                  className="bg-red-500 text-white px-2 py-1 rounded-md hover:bg-red-600"
                >
                  <FontAwesomeIcon icon={faAngleDown} />
                </button>
              </div>
            </div>
          ))
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </div>
  );
};

export default User;
