import React, { useEffect, useState } from 'react';

const User = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:3001/movies'); // Replace with your API URL
        const result = await response.json();
        setData(result); 
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleVoteChange = (index, change) => {
    const updatedData = [...data]; // Copy the current data
    updatedData[index].voting += change; // Modify the vote count
    setData(updatedData); // Update the state with the new data
  };

  return (
    <div className="min-h-screen flex flex-col items-center bg-gray-100 py-6">
      <h1 className="text-4xl font-bold mb-6">Movies List</h1>
      <div className="w-full max-w-6xl grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {data ? (
          data.map((movie, index) => (
            <div
              key={index}
              className="flex bg-white shadow-md rounded-md p-4 gap-4"
              style={{ height: '200px', width: '300px' }}
            >
              {/* Movie Poster */}
              <div className="w-24 h-36 flex-shrink-0">
                <img
                  src={movie.poster}
                  alt={movie.title}
                  className="w-full h-full object-cover rounded-md"
                />
              </div>

              {/* Movie Details */}
              <div className="flex flex-col justify-between flex-grow p-2">
                <h2 className="text-xl font-semibold">{movie.title}</h2>
                <p className="text-sm">
                  <strong>Genre:</strong> {movie.genre}
                </p>
                <p className="text-sm">
                  <strong>Director:</strong> {movie.director}
                </p>
                <p className="text-sm">
                  <strong>Starring:</strong> {movie.cast}
                </p>
                <p className="text-sm">
                  <strong>Release Date:</strong> {movie.release_date}
                </p>
                <p className="text-sm">
                  <strong>Language:</strong> {movie.language}
                </p>
                <p className="text-sm">
                  <strong>Votes:</strong> {movie.voting}
                </p>

                {/* Vote Buttons */}
                <div className="flex justify-between gap-2 mt-4">
                  <button
                    onClick={() => handleVoteChange(index, 1)}
                    className="bg-green-500 text-white px-2 py-1 rounded-md hover:bg-green-600"
                  >
                    Upvote
                  </button>
                  <button
                    onClick={() => handleVoteChange(index, -1)}
                    className="bg-red-500 text-white px-2 py-1 rounded-md hover:bg-red-600"
                  >
                    Downvote
                  </button>
                </div>
              </div>

              {/* Watch Trailer Button */}
              <div className="flex-shrink-0 self-end">
                <button className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600">
                  Watch Trailer
                </button>
              </div>
            </div>
          ))
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </div>
  );
};

export default User;
